use leptos::{component, view, IntoView};
use leptos_meta::{Meta, Title};
use leptos_router::A;

#[derive(Debug, Default, Clone)]
struct ProjectInfo {
    name: Vec<String>,
    description: Vec<String>,
    d: Vec<&'static str>,
    url: Vec<String>,
}

impl ProjectInfo {
    fn new(name: Vec<String>, description: Vec<String>, d: Vec<&'static str>, url: Vec<String>) -> Self {
        Self {
            name,
            description,
            d,
            url,
        }
    }
}

#[component]
fn ProjectSEO() -> impl IntoView {
    view! {
        <Title text="Red - Project" />
        <Meta 
            name="description"
            content="Different projects written with many different languages and tools. You can check these projects out here." 
        />
        <Meta name="keywords" content="Red, Project, Projects, Rust, Git, Backend, Frontend, Sideproject" />
        <Meta name="robots" content="index, follow" />
    }
}

